import React, { useContext } from 'react';
import './CSS/shopCatogory.css';
import { ShopContext } from '../Context/ShopContext';
import dropdown_icon from '../Component/Assets/dropdown_icon.png';
import Item from '../Component/Items/Items'; // Ensure this import is correct

const ShopCategory = (props) => {
  const { all_product } = useContext(ShopContext);

  // Function to display the price with the ₹ symbol
  const displayPrice = (price) => `₹${price}`; // Add ₹ symbol before the price

  return (
    <div className='shop-category'>
      <img src={props.banner} alt="" />
      <div className="shopcategory-indexShort">
        <p> 
          <span>Showing 1-12</span> out of {all_product.length} products
        </p>

        <div className="shopcategory-sort">
          Sort by <img src={dropdown_icon} alt="Sort Icon" />
        </div>
      </div>
      <div className="shopcategory-products">
        {all_product.map((item, i) => {
          // Logic to include checking for the category explicitly
          const isInCategory = 
            props.category === item.category || 
            (item.closet === 'womens_closet' && props.category === 'women') || 
            (item.closet === 'mens_closet' && props.category === 'men') || 
            (item.closet === 'kids_closet' && props.category === 'kids');

          if (isInCategory) {
            return (
              <div className="item" key={i}>
                <Item
                  id={item.id}
                  name={item.closet === 'mens_closet' || item.closet === 'womens_closet' || item.closet === 'kids_closet' 
                    ? 'Present in your closet' 
                    : item.name}
                  image={item.image}
                  productUrl={item.purl}
                  new_price={item.closet === 'womens_closet' ? '' : displayPrice(item.new_price)} // Don't show price for women's closet items
                />
              </div>
            );
          } else {
            return null; // Don't render anything if not in category
          }
        })}
      </div>
    </div>
  );
};

export default ShopCategory;
