import React, { useContext } from 'react';
import { ShopContext } from '../Context/ShopContext'; // Adjust the import based on your folder structure
import dropdown_icon from '../Component/Assets/dropdown_icon.png';
import Item from '../Component/Items/Items';
import './CSS/shopCategory.css'; // Corrected import statement

const ShopCategory = ({ banner, category }) => {
  // Use context to access all products, including closet items
  const { all_product } = useContext(ShopContext);

  // Handle undefined or loading state
  if (!all_product) {
    return <div>Loading...</div>; // Show loading message if products are undefined
  }

  // If all_product is an empty array
  if (all_product.length === 0) {
    return <div>No products available.</div>; // Show message if no products are found
  }

  // Function to format the price with the ₹ symbol
  const displayPrice = (price) => `₹${price}`;

  return (
    <div className='shop-category'>
      <img src={banner} alt="Shop Banner" className="shop-banner" />

      <div className="shopcategory-indexShort">
        <p>
          <span>Showing 1-12</span> out of {all_product.length} products
        </p>
        <div className="shopcategory-sort">
          Sort by <img src={dropdown_icon} alt="Sort Icon" />
        </div>
      </div>

      <div className="shopcategory-products">
        {all_product.map((item) => {
          // Determine if the item is part of the closet
          const isClosetItem = ['mens_closet', 'womens_closet', 'kids_closet'].includes(item.category);
          const isCurrentCategoryItem = category === item.category;

          // Only display items that match the current category or are closet items relevant to the current category
          if (isCurrentCategoryItem || (isClosetItem && category === item.category.split('_')[0])) {
            return (
              <div className="item" key={item.id}> {/* Use item.id as key */}
                <Item
                  id={item.id}
                  name={isClosetItem ? 'Present in your closet' : item.name} // Conditional name
                  image={item.image !== '-' ? item.image : 'https://via.placeholder.com/210'} // Placeholder for missing images
                  purl={item.purl} // Use purl directly
                  new_price={!isClosetItem ? displayPrice(item.new_price) : ''} // Conditional price display
                  old_price={!isClosetItem ? item.old_price : ''} // Conditional old price display
                  category={item.category} // Pass the category prop
                />
              </div>
            );
          }
          return null; // Return null if the item doesn't match the criteria
        })}
      </div>
    </div>
  );
};

export default ShopCategory;
