import React from 'react';
import './Items.css'; // Make sure this path is correct

const Item = ({ id, name, image, purl, new_price, old_price, category }) => {
  // Check if the item belongs to a closet category
  const isClosetItem = ['mens_closet', 'womens_closet', 'kids_closet'].includes(category);

  // Handle click event
  const handleClick = (e) => {
    if (isClosetItem) {
      e.preventDefault(); // Prevent the default action of the link
      // You can add additional logic here if needed
    }
  };

  return (
    <div className='item'>
      <img src={image !== '-' ? image : 'https://via.placeholder.com/210'} alt={name} />
      {/* Conditionally render the link based on the category */}
      {isClosetItem ? (
        <h3 onClick={handleClick} className="closet-item">{name}</h3> // Handle click for closet items
      ) : (
        <a href={purl} className="item-link">
          <h3>{name}</h3>
        </a>
      )}
      {/* Only display prices for non-closet items */}
      {!isClosetItem && (
        <div className="item-prices">
          <p className="item-price-new">{new_price}</p>
          {old_price && <p className="item-price-old">{old_price}</p>}
        </div>
      )}
    </div>
  );
};

export default Item;
