import React, { useContext } from 'react';
import './CSS/shopCatogory.css';
import { ShopContext } from '../Context/ShopContext';
import dropdown_icon from '../Component/Assets/dropdown_icon.png';
import Item from '../Component/Items/Items'; // Adjusted to correct import

const ShopCategory = (props) => {
  const { all_product } = useContext(ShopContext);

  // Function to display the price with the ₹ symbol
  const displayPrice = (price) => `₹${price}`; // Add ₹ symbol before the price

  return (
    <div className='shop-category'>
      <img src={props.banner} alt="" />
      <div className="shopcategory-indexShort">
        <p>
          <span>Showing 1-12</span> out of {all_product.length} products
        </p>

        <div className="shopcategory-sort">
          Sort by <img src={dropdown_icon} alt="Sort Icon" />
        </div>
      </div>
      <div className="shopcategory-products">
        {all_product.map((item, i) => {
          if (
            props.category === item.category || // Check if category matches props.category
            item.category === 'mens_closet' ||  // Check for mens_closet category
            item.category === 'womens_closet' || // Check for womens_closet category
            item.category === 'kids_closet'      // Check for kids_closet category
          ) {
            return (
              <div className="item" key={i}>
                <Item
                  id={item.id}
                  name={item.category === 'mens_closet' || item.category === 'womens_closet' || item.category === 'kids_closet'
                    ? 'Present in your closet' // Display this name for these categories
                    : item.name}               // Otherwise, show the actual item name

                  image={item.image}
                  productUrl={item.purl}
                  new_price={displayPrice(item.new_price)} // Display new price with ₹ symbol
                />
              </div>
            );
          } else {
            return null;
          }
        })}
      </div>
    </div>
  );
};

export default ShopCategory;
