import React, { useContext } from 'react';
import './CSS/shopCatogory.css';
import { ShopContext } from '../Context/ShopContext';
import dropdown_icon from '../Component/Assets/dropdown_icon.png';
import Item from '../Component/Items/Items';

const ShopCategory = (props) => {
  const { all_product } = useContext(ShopContext);

  // Function to format price in INR
  const formatPrice = (price) => {
    return `₹${price.toFixed(2)}`; // Format price as Indian Rupee with 2 decimal points
  };

  return (
    <div className='shop-category'>
      <img src={props.banner} alt="" />
      <div className="shopcategory-indexShort">
        <p> 
          <span>Showing 1-12</span> out of {all_product.length} products
        </p>

        <div className="shopcategory-sort">
          Sort by <img src={dropdown_icon} alt="Sort Icon" />
        </div>
      </div>
      <div className="shopcategory-products">
        {all_product.map((item, i) => {
          if (props.category === item.category || item.closet === 'mens_closet') {
            return (
              <Item
                key={i}
                id={item.id}
                name={item.closet === 'mens_closet' ? 'Present in your closet' : item.name}
                image={item.image} // Update to use the new image property
                productUrl={item.purl} // Update to use the new product URL property
                new_price={formatPrice(item.new_price)} // Format the new price to INR
                old_price={formatPrice(item.old_price)} // Format the old price to INR
              />
            );
          } else {
            return null;
          }
        })}
      </div>
    </div>
  );
};

export default ShopCategory;
