import React, { createContext } from "react";
import all_product from "../Component/Assets/all_product"; // Ensure this path is correct
import closet from "../Component/Assets/closet"; // Ensure this path is correct

export const ShopContext = createContext(null);

const ShopContextProvider = ({ children }) => {
  // Combine all_product and closet into a single array
  const combinedProducts = [...all_product, ...closet];

  const contextValue = { all_product: combinedProducts }; // Change this line to use combinedProducts

  return (
    <ShopContext.Provider value={contextValue}>
      {children} {/* Use destructured children */}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
