import React, { useContext } from 'react';
import './CSS/shopCatogory.css';
import { ShopContext } from '../Context/ShopContext';
import dropdown_icon from '../Component/Assets/dropdown_icon.png';
import Item from '../Component/Items/Items'; // Adjusted to correct import

const ShopCategory = (props) => {
  const { all_product } = useContext(ShopContext);

  // Function to display the price with the ₹ symbol
  const displayPrice = (price) => `₹${price}`;

  return (
    <div className='shop-category'>
      <img src={props.banner} alt="" />
      <div className="shopcategory-indexShort">
        <p>
          <span>Showing 1-12</span> out of {all_product.length} products
        </p>

        <div className="shopcategory-sort">
          Sort by <img src={dropdown_icon} alt="Sort Icon" />
        </div>
      </div>
      <div className="shopcategory-products">
        {all_product.map((item, i) => {
          // Check for the closet categories
          const isClosetItem = item.category === 'mens_closet' || item.category === 'womens_closet' || item.category === 'kids_closet';

          // Display only if category matches or is a closet item
          if (props.category === item.category || isClosetItem) {
            return (
              <div className="item" key={i}>
                <Item
                  id={item.id}
                  // Display 'Present in your closet' for closet categories, otherwise show the actual name
                  name={isClosetItem ? 'Present in your closet' : item.name}

                  // Display image; if the image URL is missing, provide a fallback
                  image={item.image !== '-' ? item.image : 'https://via.placeholder.com/210'} // Add placeholder if image is missing
                  productUrl={item.purl}
                  new_price={displayPrice(item.new_price)} // Display price with ₹ symbol
                />
              </div>
            );
          } else {
            return null;
          }
        })}
      </div>
    </div>
  );
};

export default ShopCategory;
