import React from 'react';
import './Items.css'; // Correctly reference the CSS file

const Item = ({ id, name, image, productUrl, new_price, old_price }) => {
  return (
    <div className='item'>
      <img src={image} alt={name} />
      <a href={productUrl} className="item-link"><h3>{name}</h3></a>
      <div className="item-prices">
        <p className="item-price-new">{new_price}</p> {/* Displaying new price */}
        {old_price && <p className="item-price-old">{old_price}</p>} {/* Conditionally display old price */}
      </div>

    </div>
  );
};

export default Item;
