import React from 'react';
import PropTypes from 'prop-types'; // Import PropTypes for prop validation
import './Items.css'; // Ensure this path is correct

const Item = ({ id, name, image, purl, new_price, old_price, category }) => {
  // Check if the item belongs to a closet category
  const isClosetItem = ['mens_closet', 'womens_closet', 'kids_closet'].includes(category);

  // Handle click event
  const handleClick = (e) => {
    if (isClosetItem) {
      e.preventDefault(); // Prevent the default action of the link
      console.log(`Clicked on closet item: ${name}`);
      // Additional logic for closet items can be added here
    }
  };

  return (
    <div className='item'>
      <img 
        src={image !== '-' ? image : 'https://via.placeholder.com/210'} 
        alt={name} 
        onError={(e) => { e.target.src = 'https://via.placeholder.com/210'; }} // Fallback image on error
      />
      {/* Conditionally render the link based on the category */}
      {isClosetItem ? (
        <h3 
          onClick={handleClick} 
          className="closet-item" 
          tabIndex={0} // Make it focusable for accessibility
        >
          {name}
        </h3>
      ) : (
        <a href={purl} className="item-link">
          <h3>{name}</h3>
        </a>
      )}
      {/* Only display prices for non-closet items */}
      {!isClosetItem && (
        <div className="item-prices">
          <p className="item-price-new">{`₹${new_price.toFixed(2)}`}</p> {/* Format new price */}
          {old_price && <p className="item-price-old">{`₹${old_price.toFixed(2)}`}</p>} {/* Format old price */}
        </div>
      )}
    </div>
  );
};

// Optional: Add prop validation
Item.propTypes = {
  id: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  image: PropTypes.string.isRequired,
  purl: PropTypes.string.isRequired,
  new_price: PropTypes.number.isRequired, // Assume new_price is a number
  old_price: PropTypes.number, // Assume old_price can be a number or undefined
  category: PropTypes.string.isRequired,
};

export default Item;
