import React, { useContext } from 'react';
import { ShopContext } from '../Context/ShopContext';
import dropdown_icon from '../Component/Assets/dropdown_icon.png';
import Item from '../Component/Items/Items';
import './CSS/shopCatogory.css'; // Corrected import statement

const ShopCategory = ({ banner, category }) => {
  const { all_product } = useContext(ShopContext);

  // Function to format the price with the ₹ symbol
  const displayPrice = (price) => `₹${price}`;

  return (
    <div className='shop-category'>
      <img src={banner} alt="Shop Banner" className="shop-banner" />

      <div className="shopcategory-indexShort">
        <p>
          <span>Showing 1-12</span> out of {all_product.length} products
        </p>
        <div className="shopcategory-sort">
          Sort by <img src={dropdown_icon} alt="Sort Icon" />
        </div>
      </div>

      <div className="shopcategory-products">
        {all_product.map((item) => {
          const isClosetItem = ['mens_closet', 'womens_closet', 'kids_closet'].includes(item.category);
          const isCurrentCategoryItem = category === item.category;

          // Display items that match the current category or closet items related to the current category
          if (
            isCurrentCategoryItem || 
            (isClosetItem && 
              ((category === 'mens' && item.category === 'mens_closet') ||
              (category === 'womens' && item.category === 'womens_closet') ||
              (category === 'kids' && item.category === 'kids_closet')))
          ) {
            return (
              <div className="item" key={item.id}>
                <Item
                  id={item.id}
                  name={isClosetItem ? 'Present in your closet' : item.name}
                  image={item.image !== '-' ? item.image : 'https://via.placeholder.com/210'}
                  purl={item.purl} // Use purl directly
                  new_price={!isClosetItem ? displayPrice(item.new_price) : ''}
                  old_price={!isClosetItem ? item.old_price : ''}
                  category={item.category}
                />
              </div>
            );
          }
          return null; // Return null if the item doesn't match the criteria
        })}
      </div>
    </div>
  );
};

export default ShopCategory;
