import React from 'react';
import './Item.css'; // Make sure to add appropriate styles if necessary

const Item = ({ id, name, image, productUrl, new_price, old_price }) => {
  return (
    <div className="item">
      <a href={productUrl} target="_blank" rel="noopener noreferrer">
        <img src={image} alt={name} />
      </a>
      <h3>{name}</h3>
      {new_price && old_price && (
        <div className="item-pricing">
          <span className="new-price">${new_price.toFixed(2)}</span>
          <span className="old-price">${old_price.toFixed(2)}</span>
        </div>
      )}
    </div>
  );
};

export default Item;
