import React, { useState } from 'react';
import './Navbar.css';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';
import { useLocation } from 'react-router-dom';

const Navbar = () => {
  const location = useLocation();
  const [menu, setMenu] = useState(location.pathname.substring(1) || 'shop');

  useEffect(() => {
    setMenu(location.pathname.substring(1) || 'shop');
  }, [location]);

  return (
    <div className='navbar'>
      <div className='nav-logo'>
        <img src={logo} alt="StyleStitch logo" />
        <p>StyleStitch</p>
      </div>
      <ul className='nav-menu'>
        <li onClick={() => { setMenu('shop'); }}>
          <Link to='/'>Shop</Link> {menu === 'shop' ? <hr /> : <> </>}
        </li>
        <li onClick={() => { setMenu('men'); }}>
          <Link to='/mens'>Men</Link> {menu === 'men' ? <hr /> : <> </>}
        </li>
        <li onClick={() => { setMenu('womens'); }}>
          <Link to='/womens'>Women</Link> {menu === 'womens' ? <hr /> : <> </>}
        </li>
        <li onClick={() => { setMenu('kids'); }}>
          <Link to='/kids'>Kids</Link> {menu === 'kids' ? <hr /> : <> </>}
        </li>
      </ul>
      <div className='nav-login-cart'>
        <Link to='/login'><button>Login</button></Link>
        <Link to='/cart'><img className='cart-icon' src={cart_icon} alt="Cart icon" /></Link>
        <div className='nav-cart-count'>0</div>
      </div>
    </div>
  );
};

export default Navbar;
