// Item.js
import React from 'react';
import PropTypes from 'prop-types'; // Import PropTypes for validation
import './Items.css'; // Ensure correct path for CSS

const Item = ({ id, name, image, purl, new_price, old_price, category }) => {
  const isClosetItem = ['mens_closet', 'womens_closet', 'kids_closet'].includes(category);

  const handleClick = (e) => {
    if (isClosetItem) {
      e.preventDefault(); // Prevent link navigation for closet items
      console.log(`Clicked on closet item: ${name}`);
      // Additional logic for closet items can go here
    }
  };

  const newPrice = parseFloat(new_price);
  const oldPrice = old_price ? parseFloat(old_price) : null;

  return (
    <div className='item'>
      <img 
        src={image !== '-' ? image : 'https://via.placeholder.com/210'} 
        alt={name}
        onError={(e) => { e.target.src = 'https://via.placeholder.com/210'; }} 
      />
      {isClosetItem ? (
        <h3 onClick={handleClick} className="closet-item" tabIndex={0}>
          {name}
        </h3>
      ) : (
        <a href={purl} className="item-link">
          <h3>{name}</h3>
        </a>
      )}
      {!isClosetItem && (
        <div className="item-prices">
          {newPrice && !isNaN(newPrice) ? (
            <p className="item-price-new">{`₹${newPrice.toFixed(2)}`}</p>
          ) : (
            <p className="item-price-new">Price not available</p>
          )}
          {oldPrice && !isNaN(oldPrice) && (
            <p className="item-price-old">{`₹${oldPrice.toFixed(2)}`}</p>
          )}
        </div>
      )}
    </div>
  );
};

Item.propTypes = {
  id: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  image: PropTypes.string.isRequired,
  purl: PropTypes.string.isRequired,
  new_price: PropTypes.oneOfType([PropTypes.number, PropTypes.string]).isRequired,
  old_price: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
  category: PropTypes.string.isRequired,
};

export default Item;
